import React from 'react';

const userInput = (props) => {
   const inputStyle={
         border: '2px solid red',
         textAlign: 'center',
         boxShadow: '10px 10px lightred'
   }

   return <input type="text" 
   style={inputStyle}
   onChange={props.changed}
   value={props.currentName}/>
}
export default userInput;
